// CardDiscard.js

import React, { useRef } from 'react';
import Draggable from './Draggable';
import CardDropZone from './CardDropZone';


const CardDiscard = ({ position }) => {
    const discardRef = useRef();
    const thisSize = ({ x: 110, y: 100 });

    const [isCardOver, setIsCardOver] = useState(false);

    const handleCardEnter = () => {
        setIsCardOver(true);
    };

    const handleCardLeave = () => {
        setIsCardOver(false);
    };

    const handleCardDrop = (cardText) => {
        console.log(`Card dropped onto Category: ${cardText}`);
    };


    return (
        <Draggable>
            <div
                ref={discardRef}
                style={{
                    position: 'absolute',
                    width: thisSize.x,
                    height: thisSize.y,
                    left: position.x + 'px',
                    top: position.y + 'px',

                    // to adapt in css file
                    border: '1px solid black',
                    backgroundColor: 'red',
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'space-around',
                    alignItems: 'center',
                }}
            >
                Discard
                <CardDropZone />
            </div>
        </Draggable>
    );
};

export default CardDiscard;
