// GamePage.js

import React, { useState } from 'react';
import Card from './Card';
import CardStack from './CardStack';


const GamePage = () => {

    const handleAddCard = () => {
        if (cardList.length > 0) {
            // Piochez la carte du dessus du deck
            const drawnCard = cardList.pop();

            // Mettez à jour la liste des cartes et ajoutez la carte à la position du CardStack
            setCardList([...cardList]);
            setCardList((prev) => [...prev, { ...drawnCard, isDrawn: true }]);
        } else {
            alert('Le deck est vide.');
        }
    };

    return (
        <div>
            <CardStack addCard={handleAddCard}/>
            {cardList.map((card) => (
                <Card key={card.id} text={card.text} />
            ))}
        </div>
    );
};

export default GamePage;
